import React, {Component} from 'react';
 const { cmd } = require('node-cmd');

class Home extends Component {
    state = {
        output: "Nothing to show"
    };

    handleLaunch = () => {
        //this.setState({output : "launch clicked"});
         /*
         const spawn = require("child_process").spawn
         const pythonProcess = spawn('python', ["C:\\Users\\redemption\\Downloads\\scalable web apps\\trainNeuralTranslation.py"]);

         pythonProcess.stdout.on('data', (data) => {
              console.log(data.toString());
             this.setState({output : data})
         });
          */




        /* const express = require('express');
         const app = express();

         app.get('/', (req, res) => {

             const { spawn } = require('child_process');
             const pyProg = spawn('python', ['C:\\Users\\redemption\\Downloads\\scalable web apps\\trainNeuralTranslation.py']);

             pyProg.stdout.on('data', function(data) {

                 console.log(data.toString());
                 res.write(data);
                 res.end('end');
             });
         })

         app.listen(3000, () => console.log('Application listening on port 3000')); */
        /*
         const express = require('express');
         const app = express();

         let runPy = new Promise(function (success, nosuccess) {

             const { spawn } = require('child_process');
             const pyProg = spawn('python', ['C:\\Users\\redemption\\Downloads\\scalable web apps\\trainNeuralTranslation.py']);

             pyProg.stdout.on('data', function (data) {
                 success(data);
             });

             pyProg.stderr.on('data', (data) => {
                 nosuccess(data);
             });

         });

         app.get('/', (req, res) => {
             res.write('welcome\n');

             runPy.then(function (fromRunpy) {
                 console.log(fromRunpy.toString());
                 res.end(fromRunpy);
             });
         })

         app.listen(3000, () => console.log('Application listening on port 3000!'))*/

       /*let myPythonScriptPath = 'C:\\Users\\redemption\\Downloads\\scalable web apps\\trainNeuralTranslation.py';

        // Use python shell
        const {PythonShell} = require("python-shell");
        let pyshell = new PythonShell(myPythonScriptPath);

        pyshell.on('message', function (message) {
            // received a message sent from the Python script (a simple "print" statement)
            console.log(message);
        });

        // end the input stream and allow the process to exit
        pyshell.end(function (err) {
            if (err) {
                throw err;
            }
            ;

            console.log('finished');
        });*/



        let pyProcess = cmd.get('hello.py',
              function(data, err, stderr) {
                if (!err) {
                  console.log("data from python script " + data)
                } else {
                  console.log("python script cmd error: " + err)
                  }
                }
              );







    };

    handleEvaluate = () => {
        this.setState({output: "evaluate clicked"})
    };

    handleReverse = () => {
        this.setState({output: "reverse clicked"})
    };

    handleEpoch = () => {
        this.setState({output: "epoch clicked"})
    };

    render() {
        return (

            <div className="mainContent">
                <div id="title">
                    <h1>English to German Translator</h1>
                </div>
                <div id="buttons">
                    <button className="btn-primary" onClick={this.handleLaunch}>Launch</button>
                    <button className="btn-primary ml13" onClick={this.handleEvaluate}>Evaluate</button>
                    <button className="btn-primary ml13" onClick={this.handleReverse}>Reverse Phrase Order</button>
                    <button className="btn-primary ml13" onClick={this.handleEpoch}>Set Epochs</button>
                </div>
                <p>Display Output:</p>
                <div id="content">
                    {this.state.output}
                </div>
            </div>

        );
    }
}

export default Home;
