import React, {Component} from 'react';
import axios from 'axios';
 //const cmd = require('node-cmd');

class Home extends Component {
    state = {
        output: "Nothing to show",
        result: []
    };

    componentDidMount() {

    axios.get("http://localhost:3001")
      .then(result => this.setState({result: result.request}))

      .catch(error => console.log(error));
  }

    handleLaunch = () => {
        var express = require('express');
        let { PythonShell } = require('python-shell');
        let fs = require('fs');
        var app = express();
        var output;
        var pyshell = new PythonShell('hello.py');

        pyshell.on('message', function (message) {
                     console.log(message);
                      output = message;
                    }); //end of pyshell function

         app.get('/', (req, res) => {

                    fs.readFile('../../public/index.html', function(err, data) {
                    res.writeHead(200, {'Content-Type': 'text/html'});
                    res.write(data);
                    res.write("<div id='content'>"+output+"</div>");
                    res.end();
                  });
        });


        app.listen(3000, function () {
            console.log(`server running on port 3001`);
        })
    };

    handleEvaluate = () => {
        this.setState({output: "evaluate clicked"})
    };

    handleReverse = () => {
        this.setState({output: "reverse clicked"})
    };

    handleEpoch = () => {
        this.setState({output: "epoch clicked"})
    };

    render() {
        return (

            <div className="mainContent">
                <div id="title">
                    <h1>English to German Translator</h1>
                </div>
                <div id="buttons">
                    <button className="btn-primary" onClick={this.handleLaunch}>Launch</button>
                    <button className="btn-primary ml13" onClick={this.handleEvaluate}>Evaluate</button>
                    <button className="btn-primary ml13" onClick={this.handleReverse}>Reverse Phrase Order</button>
                    <button className="btn-primary ml13" onClick={this.handleEpoch}>Set Epochs</button>
                </div>
                <p>Display Output:</p>
                <div id="content">
                    {this.state.output}
                    {this.state.result}
                </div>
            </div>

        );
    }
}

export default Home;
