import React, {Component} from 'react';
import axios from 'axios';
 //const cmd = require('node-cmd');

class Home extends Component {
    state = {
        output: "Nothing to show",
        result: []
    };

    componentDidMount() {

    axios.get("http://localhost:3001")
      .then(result => this.setState({result: result.data}))

      .catch(error => console.log(error));
  }

    handleLaunch = () => {

    };

    handleEvaluate = () => {
        this.setState({output: "evaluate clicked"})
    };

    handleReverse = () => {
        this.setState({output: "reverse clicked"})
    };

    handleEpoch = () => {
        this.setState({output: "epoch clicked"})
    };

    render() {
        return (

            <div className="mainContent">
                <div id="title">
                    <h1>English to German Translator</h1>
                </div>
                <div id="buttons">
                    <button className="btn-primary" onClick={this.componentDidMount}>Launch</button>
                    <button className="btn-primary ml13" onClick={this.handleEvaluate}>Evaluate</button>
                    <button className="btn-primary ml13" onClick={this.handleReverse}>Reverse Phrase Order</button>
                    <button className="btn-primary ml13" onClick={this.handleEpoch}>Set Epochs</button>
                </div>
                <p>Display Output:</p>
                <div id="content">
                    {this.state.output}
                    {this.state.result}
                </div>
            </div>

        );
    }
}

export default Home;
